      function displayqTip(elem, dadiv) {
			var box=document.getElementById(dadiv);
            jQuery(elem).qtip(jQuery.extend(true, templates.tooltips, {
               content: { text: box.innerHTML, title: { text: 'Notes:', button: '<img src="https://law.gsu.edu/images/close.gif" alt="close" />' }} ,
				show: { delay: 0, ready: true },
				hide: { when: { event: 'unfocus' } },
				api: { onHide:  function() { jQuery(elem).qtip('destroy'); } }
            }));
        }

var key_press=false;
function jump_menu(target, sel_obj) {
    if (!key_press) {
        eval(target + ".location='" + sel_obj.options[sel_obj.selectedIndex].value + "'");
    }
}
function key(evt, sel_obj) {
    var key_code = document.layers ? evt.which : evt.keyCode;
    if (key_code == 13) {
        sel_obj.form.submit();
    } else {
        key_press = true;
        return false;
    }
}
function redir(url){ //v3.0
	if (url.length > 5)
		window.location = url;
}

function cssc_terms($term) {
	$term_year = substr($term, 0, 4);
	$term_sems = substr($term, 4, 2);
	switch ($term_sems) {
		case '01';
			$term_sems = 'Spring';
			break;
		case '05';
			$term_sems = 'Summer';
			break;
		case '08';
			$term_sems = 'Fall';
			break;
		default: 
			$term_sems = '';
	}
return "$term_sems $term_year";
}
